import React, { useState } from 'react';

const BookDemoForm: React.FC = () => {
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    const form = e.target as HTMLFormElement;
    const data = {
      user_name: form.user_name.value,
      user_email: form.user_email.value,
      user_phone: form.user_phone.value,
    };
    try {
      const res = await fetch('http://localhost:5000/send-email', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });
      if (res.ok) {
        setSubmitted(true);
      } else {
        setError('Failed to send email. Please try again.');
      }
    } catch (err) {
      setError('Failed to send email. Please try again.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-grafanaBg py-16 px-4">
      <div className="bg-grafanaGray rounded-2xl p-8 max-w-md w-full shadow-2xl border border-grafanaBlue/30">
        <h1 className="text-3xl md:text-4xl font-bold text-center mb-6 bg-gradient-to-r from-grafanaBlue via-blue-400 to-blue-300 bg-clip-text text-transparent">Book a Free Demo</h1>
        <p className="text-gray-300 text-center mb-8">Experience our teaching style and get your questions answeredâ€”no commitment required!</p>
        {submitted ? (
          <div className="text-center text-lg text-grafanaBlue font-semibold py-8">
            Thank you! We will connect with you within 24 hours.
          </div>
        ) : (
          <form className="space-y-4" onSubmit={handleSubmit}>
            <input name="user_name" type="text" placeholder="Your Name" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
            <input name="user_email" type="email" placeholder="Your Email" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
            <input name="user_phone" type="tel" placeholder="Your Phone" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
            <button type="submit" className="w-full bg-gradient-to-r from-grafanaBlue via-blue-400 to-blue-300 text-white py-3 rounded-full text-lg font-semibold hover:shadow-grafana transition-all duration-300 transform hover:scale-105 mt-2">Submit</button>
          </form>
        )}
        {error && <div className="text-red-500 text-center mt-4">{error}</div>}
      </div>
    </div>
  );
};

export default BookDemoForm;
