import React from 'react';

const CareerAddonsSection: React.FC = () => {
  const renderIcon = (iconType: string) => {
    switch (iconType) {
      case 'resume':
        return (
          <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
            <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8l-6-6zm-1 1v5h5v10H6V3h7z"/>
            <path d="M8 12h8v2H8zm0 4h6v2H8zm0-8h4v2H8z"/>
          </svg>
        );
      case 'linkedin':
        return (
          <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
            <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
          </svg>
        );
      case 'interview':
        return (
          <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
            <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
            <path d="M8 10h8v2H8zm0 4h6v2H8z"/>
          </svg>
        );
      case 'mentoring':
        return (
          <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
            <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.94-.49-7-3.85-7-7.93 0-.62.08-1.21.21-1.79L9 15v1c0 1.1.9 2 2 2v1.93zm6.9-2.54c-.26-.81-1-1.39-1.9-1.39h-1v-3c0-.55-.45-1-1-1H8v-2h2c.55 0 1-.45 1-1V7h2c1.1 0 2-.9 2-2v-.41c2.93 1.19 5 4.06 5 7.41 0 2.08-.8 3.97-2.1 5.39z"/>
          </svg>
        );
      case 'placement':
        return (
          <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
            <path d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm-5 14H7v-2h7v2zm3-4H7v-2h10v2zm0-4H7V7h10v2z"/>
            <path d="M9 9h6v2H9zm0 4h6v2H9z"/>
          </svg>
        );
      default:
        return null;
    }
  };

  const addons = [
    {
      id: 1,
      title: 'Resume Prep',
      description: 'Professional resume crafting with ATS optimization and industry-specific templates',
      icon: 'resume',
      features: ['ATS-friendly format', 'Industry templates', 'Expert review', 'Multiple versions']
    },
    {
      id: 2,
      title: 'LinkedIn & Naukri Optimization',
      description: 'Optimize your professional profiles to attract top recruiters and opportunities',
      icon: 'linkedin',
      features: ['Profile optimization', 'Keyword strategy', 'Network building', 'Content guidance']
    },
    {
      id: 3,
      title: 'Technical + HR Interview Prep',
      description: 'Comprehensive interview preparation covering both technical skills and soft skills',
      icon: 'interview',
      features: ['Mock interviews', 'Technical questions', 'HR scenarios', 'Feedback sessions']
    },
    {
      id: 4,
      title: '1-on-1 Review Meetings',
      description: 'Personalized mentoring sessions with industry experts and career coaches',
      icon: 'mentoring',
      features: ['Personal mentoring', 'Career guidance', 'Skill assessment', 'Goal setting']
    },
    {
      id: 5,
      title: 'Job Placement Support',
      description: 'Direct placement assistance with our network of 500+ hiring companies',
      icon: 'placement',
      features: ['Direct referrals', 'Interview scheduling', 'Salary negotiation', 'Onboarding support']
    }
  ];

  return (
    <section id="career-addons" className="py-20 bg-grafanaBg scroll-mt-24">
      <div className="container mx-auto px-4">
        {/* Section Header */}
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-display font-bold mb-6">
            <span className="bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
              Career Success
            </span>
            <br />
            <span className="bg-gradient-to-r from-grafanaGreen to-grafanaOrange bg-clip-text text-transparent">
              Add-ons
            </span>
          </h2>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto">
            Beyond technical skills, we provide comprehensive career support to ensure your success in the job market.
          </p>
        </div>

        {/* First Row - First 3 Items */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-8">
          {addons.slice(0, 3).map((addon) => (
            <div
              key={addon.id}
              className="bg-grafanaGray rounded-xl p-8 border border-gray-700 hover:border-grafanaGreen/50 transition-all duration-300 hover:transform hover:scale-105 group relative overflow-hidden"
            >
              {/* Background gradient on hover */}
              <div className="absolute inset-0 bg-gradient-to-br from-grafanaGreen/5 to-grafanaBlue/5 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

              {/* Content */}
              <div className="relative z-10">
                {/* Icon */}
