import React from 'react';
// @ts-ignore
import linkedinLogo from '../assets/logo_addons/linkedin-logo.svg';
// @ts-ignore
import naukriLogo from '../assets/logo_addons/naukri-seeklogo.svg';
// @ts-ignore
import techInterviewLogo from '../assets/logo_addons/tech-interview-logo.svg';
// @ts-ignore
import resumeLogo from '../assets/logo_addons/resume-logo.svg';

// SVG logo URLs (to be replaced by user-provided SVGs later)
const hrLogo = 'https://www.svgrepo.com/show/3870/managers.svg';
const techLogo = 'https://www.svgrepo.com/show/5238/coding.svg';
const placementLogo = 'https://www.svgrepo.com/show/45083/project-manager.svg';
const mentoringLogo = 'https://www.svgrepo.com/show/354115/mentoring.svg';
const techSupportLogo = 'https://www.svgrepo.com/show/354111/technical-support.svg';

const addons = [
  {
    id: 1,
    title: 'LinkedIn Optimization',
    description: 'Optimize your LinkedIn profile to attract top recruiters and opportunities.',
    logo: linkedinLogo,
    features: [
      'Profile optimization',
      'Keyword strategy',
      'Network building',
      'Content guidance',
    ],
    badge: 'Profile',
  },
  {
    id: 2,
    title: 'Naukri Optimization',
    description: 'Boost your visibility and job matches on Naukri with a standout profile.',
    logo: naukriLogo,
    features: [
      'Profile ranking boost',
      'ATS optimization',
      'Recruiter targeting',
      'Profile completeness',
    ],
    badge: 'Profile',
  },
  {
    id: 3,
    title: 'HR Interview Prep',
    description: 'Ace your HR interviews with expert guidance and mock sessions.',
    logo: '/assets/logo_addons/hr-interview-logo.svg', // Add this SVG to your assets
    features: [
      'Mock HR interviews',
      'Behavioral question prep',
      'Feedback & improvement',
      'Confidence building',
    ],
    badge: 'Interview',
  },
  {
    id: 4,
    title: 'Technical Interview Prep',
    description: 'Crack technical interviews with coding, system design, and problem-solving practice.',
    logo: techInterviewLogo,
    features: [
      'Coding rounds',
      'System design',
      'DSA practice',
      'Expert feedback',
    ],
    badge: 'Interview',
  },
  {
    id: 5,
    title: '1-on-1 Review Meetings',
    description: 'Personalized sessions to review your progress and resolve doubts.',
    logo: '/assets/logo_addons/mentor-logo.svg', // Add this SVG to your assets
    features: [
      'Progress tracking',
      'Goal setting',
      'Direct mentorship',
      'Actionable feedback',
    ],
    badge: 'Mentoring',
  },
  {
    id: 6,
    title: 'Tech Support',
    description: 'Get help with technical issues, assignments, and projects anytime.',
    logo: '/assets/logo_addons/tech-support-logo.svg', // Add this SVG to your assets
    features: [
      '24/7 support',
      'Assignment help',
      'Project guidance',
      'Live troubleshooting',
    ],
    badge: 'Support',
  },
  {
    id: 7,
