import React from 'react';

const EnrollNowForm: React.FC = () => {
  return (
    <div className="min-h-screen flex items-center justify-center bg-grafanaBg py-16 px-4">
      <div className="bg-grafanaGray rounded-2xl p-8 max-w-md w-full shadow-2xl border border-grafanaBlue/30">
        <h1 className="text-3xl md:text-4xl font-bold text-center mb-6 bg-gradient-to-r from-grafanaBlue via-blue-400 to-blue-300 bg-clip-text text-transparent">Enroll Now</h1>
        <p className="text-gray-300 text-center mb-8">Take the first step toward your dream tech career. Fill out the form to enroll now!</p>
        <form className="space-y-4">
          <input type="text" placeholder="First Name" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
          <input type="text" placeholder="Last Name" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
          <input type="tel" placeholder="Mobile Number" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
          <input type="email" placeholder="Email ID" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
          <button type="submit" className="w-full bg-gradient-to-r from-grafanaBlue via-blue-400 to-blue-300 text-white py-3 rounded-full text-lg font-semibold hover:shadow-grafana transition-all duration-300 transform hover:scale-105 mt-2">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default EnrollNowForm;
