import React from 'react';

// Platform Logo Components
const RHLogo = () => (
  <svg viewBox="0 0 24 24" fill="currentColor" className="w-8 h-8">
    <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
  </svg>
);

const DockerLogo = () => (
  <svg viewBox="0 0 24 24" fill="currentColor" className="w-8 h-8">
    <path d="M13.983 11.078h2.119a.186.186 0 00.186-.185V9.006a.186.186 0 00-.186-.186h-2.119a.186.186 0 00-.185.185v1.888c0 .102.083.185.185.185zm-2.954-5.43h2.118a.186.186 0 00.186-.186V3.574a.186.186 0 00-.186-.185h-2.118a.186.186 0 00-.186.185v1.888c0 .102.083.185.186.185zm0 2.715h2.118a.186.186 0 00.186-.185V6.29a.186.186 0 00-.186-.186h-2.118a.186.186 0 00-.186.186v1.887c0 .102.083.185.186.185zm-2.93 0h2.12a.186.186 0 00.185-.185V6.29a.186.186 0 00-.186-.186H8.1a.186.186 0 00-.185.186v1.887c0 .102.082.185.185.185zm0 2.715h2.12a.186.186 0 00.185-.185V9.006a.186.186 0 00-.186-.186H8.1a.186.186 0 00-.185.185v1.888c0 .102.082.185.185.185z"/>
  </svg>
);

const AzureLogo = () => (
  <svg viewBox="0 0 24 24" fill="currentColor" className="w-8 h-8">
    <path d="M14.5 3.75L13.25 4.61L14.5 5.47L15.75 4.61L14.5 3.75M14.5 8.25L13.25 9.11L14.5 9.97L15.75 9.11L14.5 8.25M9.5 6L8.25 6.86L9.5 7.72L10.75 6.86L9.5 6M19.5 6L18.25 6.86L19.5 7.72L20.75 6.86L19.5 6M14.5 12.75L13.25 13.61L14.5 14.47L15.75 13.61L14.5 12.75M9.5 15L8.25 15.86L9.5 16.72L10.75 15.86L9.5 15M19.5 15L18.25 15.86L19.5 16.72L20.75 15.86L19.5 15M14.5 20.25L13.25 21.11L14.5 21.97L15.75 21.11L14.5 20.25M9.5 22.5L8.25 23.36L9.5 24.22L10.75 23.36L9.5 22.5M19.5 22.5L18.25 23.36L19.5 24.22L20.75 23.36L19.5 22.5Z"/>
  </svg>
);

const GitLabLogo = () => (
  <svg viewBox="0 0 24 24" fill="currentColor" className="w-8 h-8">
    <path d="M22.65 14.39L12 22.13 1.35 14.39a.84.84 0 0 1-.3-.94l1.22-3.78 2.44-7.51A.42.42 0 0 1 4.82 2a.43.43 0 0 1 .58 0 .42.42 0 0 1 .11.18l2.44 7.49h8.1l2.44-7.51A.42.42 0 0 1 18.6 2a.43.43 0 0 1 .58 0 .42.42 0 0 1 .11.18l2.44 7.51L22.35 13.5a.84.84 0 0 1-.3.94z"/>
  </svg>
);

const PrometheusLogo = () => (
  <svg viewBox="0 0 24 24" fill="currentColor" className="w-8 h-8">
    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
  </svg>
);

const GrafanaLogo = () => (
  <svg viewBox="0 0 24 24" fill="currentColor" className="w-8 h-8">
    <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
  </svg>
);

const TerraformLogo = () => (
  <svg viewBox="0 0 24 24" fill="currentColor" className="w-8 h-8">
    <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
  </svg>
);

const AzureCertLogo = () => (
  <svg viewBox="0 0 24 24" fill="currentColor" className="w-8 h-8">
    <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
  </svg>
);

const CoursesSection: React.FC = () => {
  const courses = [
    {
      id: 1,
      name: 'RHEL',
      description: 'Red Hat Enterprise Linux administration and management',
      logo: <RHLogo />,
      color: 'from-red-500 to-red-600',
      duration: '4 weeks',
      level: 'Intermediate'
    },
    {
      id: 2,
      name: 'Docker',
      description: 'Containerization and container orchestration',
      logo: <DockerLogo />,
      color: 'from-blue-500 to-blue-600',
      duration: '3 weeks',
      level: 'Beginner'
    },
    {
      id: 3,
      name: 'Azure + Kubernetes',
      description: 'Microsoft Azure cloud and Kubernetes orchestration',
      logo: <AzureLogo />,
      color: 'from-blue-600 to-blue-700',
      duration: '6 weeks',
      level: 'Advanced'
    },
    {
      id: 4,
      name: 'GitLab CI/CD',
      description: 'Continuous Integration and Continuous Deployment',
      logo: <GitLabLogo />,
      color: 'from-orange-500 to-orange-600',
      duration: '3 weeks',
      level: 'Intermediate'
    },
    {
      id: 5,
      name: 'Prometheus',
      description: 'Monitoring and alerting toolkit',
      logo: <PrometheusLogo />,
      color: 'from-red-600 to-red-700',
      duration: '2 weeks',
      level: 'Intermediate'
    },
    {
      id: 6,
      name: 'Grafana',
      description: 'Data visualization and analytics platform',
      logo: <GrafanaLogo />,
      color: 'from-orange-400 to-orange-500',
      duration: '2 weeks',
      level: 'Intermediate'
    },
    {
      id: 7,
      name: 'Terraform',
      description: 'Infrastructure as Code automation',
      logo: <TerraformLogo />,
      color: 'from-purple-500 to-purple-600',
      duration: '4 weeks',
      level: 'Advanced'
    },
    {
      id: 8,
      name: 'AZ-104',
      description: 'Microsoft Azure Administrator certification',
      logo: <AzureCertLogo />,
      color: 'from-blue-500 to-blue-600',
      duration: '8 weeks',
      level: 'Advanced'
    }
  ];

  return (
    <section id="courses" className="py-20 bg-grafanaGray">
      <div className="container mx-auto px-4">
        {/* Section Header */}
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-display font-bold mb-6">
            <span className="bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
              Master the Most
            </span>
