import React from 'react';

// Official/brand-like SVG logo URLs
const linkedInLogo = 'https://www.svgrepo.com/show/157006/linkedin.svg';
const naukriLogo = 'https://upload.wikimedia.org/wikipedia/commons/6/64/Naukri_Logo.svg'; // More brand-like
const hrLogo = 'https://www.svgrepo.com/show/3870/managers.svg';
const techLogo = 'https://www.svgrepo.com/show/5238/coding.svg';
const placementLogo = 'https://www.svgrepo.com/show/45083/project-manager.svg';
const mentoringLogo = 'https://www.svgrepo.com/show/354115/mentoring.svg';
const techSupportLogo = 'https://www.svgrepo.com/show/354111/technical-support.svg';

const addons = [
  {
    id: 1,
    title: 'LinkedIn Optimization',
    description: 'Optimize your LinkedIn profile to attract top recruiters and opportunities.',
    logo: linkedInLogo,
    features: [
      'Profile optimization',
      'Keyword strategy',
      'Network building',
      'Content guidance',
    ],
  },
  {
    id: 2,
    title: 'Naukri Optimization',
    description: 'Boost your visibility and job matches on Naukri with a standout profile.',
    logo: naukriLogo,
    features: [
      'Profile optimization',
      'Recruiter targeting',
      'Resume upload tips',
      'Alert setup',
    ],
  },
  {
    id: 3,
    title: 'HR Interview Prep',
    description: 'Master HR interviews with mock sessions and expert feedback.',
    logo: hrLogo,
    features: [
      'Mock HR interviews',
      'Soft skills training',
      'Behavioral questions',
      'Feedback sessions',
    ],
  },
  {
    id: 4,
    title: 'Technical Interview Prep',
    description: 'Sharpen your technical skills and ace coding interviews.',
    logo: techLogo,
    features: [
      'Mock technical interviews',
      'Coding challenges',
      'System design practice',
      'Expert feedback',
    ],
  },
  {
    id: 5,
    title: '1-on-1 Review Meetings',
    description: 'Personalized mentoring sessions with industry experts and career coaches.',
    logo: mentoringLogo,
    features: [
      'Personal mentoring',
      'Career guidance',
      'Skill assessment',
      'Goal setting',
    ],
  },
  {
    id: 6,
    title: 'Tech Support',
    description: 'Get help with technical issues and doubts from our expert support team.',
    logo: techSupportLogo,
    features: [
      '24/7 support',
      'Doubt resolution',
      'Live troubleshooting',
      'Resource guidance',
    ],
  },
  {
    id: 7,
    title: 'Job Placement Support',
    description: 'Get direct placement assistance with our network of 500+ hiring companies.',
    logo: placementLogo,
    features: [
      'Direct referrals',
      'Interview scheduling',
      'Salary negotiation',
      'Onboarding support',
    ],
  },
];

const CareerAddonsSection: React.FC = () => {
  return (
    <section id="career-addons" className="py-20 bg-grafanaBg scroll-mt-24">
      <div className="container mx-auto px-4">
        {/* Section Header */}
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-display font-bold mb-6">
            <span className="bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
              Career Success
            </span>
            <br />
            <span className="bg-gradient-to-r from-grafanaGreen to-grafanaOrange bg-clip-text text-transparent">
              Add-ons
            </span>
          </h2>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto">
            Beyond technical skills, we provide comprehensive career support to ensure your success in the job market.
          </p>
        </div>

        {/* Addons Grid - match CoursesSection */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {addons.map((addon) => (
            <div
              key={addon.id}
              className="relative bg-grafanaBg rounded-xl p-6 border border-gray-700 hover:border-grafanaBlue/50 transition-all duration-500 hover:transform hover:scale-105 group overflow-hidden flex flex-col h-full"
            >
              {/* Hover Background Effect */}
              <div className="absolute inset-0 bg-gradient-to-br from-grafanaBlue/10 via-grafanaGreen/10 to-grafanaOrange/10 opacity-0 group-hover:opacity-100 transition-all duration-500"></div>
              {/* Animated Border Effect */}
              <div className="absolute inset-0 rounded-xl bg-gradient-to-r from-grafanaBlue/30 via-grafanaGreen/30 to-grafanaOrange/30 opacity-0 group-hover:opacity-100 transition-all duration-500"></div>

              {/* Addon Logo */}
              <div className="relative w-16 h-16 rounded-xl bg-gradient-to-br from-grafanaBlue/10 to-grafanaGreen/10 flex items-center justify-center mb-4 group-hover:scale-110 group-hover:rotate-3 transition-all duration-500">
                <img src={addon.logo} alt={`${addon.title} logo`} className="w-10 h-10 group-hover:drop-shadow-lg transition-all duration-300" />
              </div>

              {/* Addon Info */}
              <div className="relative flex flex-col flex-1">
                <h3 className="text-xl font-semibold text-white mb-2 group-hover:text-grafanaBlue transition-all duration-300 group-hover:scale-105">
                  {addon.title}
                </h3>
                <p className="text-gray-400 text-sm mb-4 leading-relaxed group-hover:text-gray-300 transition-colors duration-300 flex-1">
                  {addon.description}
                </p>

                {/* Addon Features */}
                <ul className="space-y-2 mt-auto">
                  {addon.features.map((feature, index) => (
                    <li key={index} className="flex items-start text-xs text-gray-300 hover:text-grafanaGreen transition-colors duration-200 group/item">
                      <div className="w-2 h-2 bg-grafanaGreen rounded-full mr-3 mt-1.5 flex-shrink-0 group-hover/item:bg-grafanaBlue transition-colors duration-200"></div>
                      <span className="text-left leading-tight">{feature}</span>
                    </li>
                  ))}
                </ul>
              </div>

              {/* Floating Elements on Hover */}
              <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-all duration-500 delay-200">
                <div className="w-2 h-2 bg-grafanaGreen rounded-full animate-pulse"></div>
              </div>
              <div className="absolute bottom-2 left-2 opacity-0 group-hover:opacity-100 transition-all duration-500 delay-300">
                <div className="w-1 h-1 bg-grafanaBlue rounded-full animate-pulse"></div>
              </div>
            </div>
          ))}
        </div>

        {/* CTA */}
        <div className="text-center mt-16">
          <div className="bg-grafanaGray rounded-2xl p-8 border border-grafanaGreen/30 max-w-2xl mx-auto">
            <h3 className="text-2xl font-semibold text-white mb-4">
              Ready to Accelerate Your Career?
            </h3>
            <p className="text-gray-300 mb-6">
              Get access to all career add-ons and personalized support to land your dream job.
            </p>
            <button className="bg-gradient-to-r from-grafanaGreen to-grafanaBlue text-white px-8 py-4 rounded-full text-lg font-semibold hover:shadow-grafana transition-all duration-300 transform hover:scale-105">
              Get Career Support
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default CareerAddonsSection;
