import React, { useState } from 'react';
import emailjs from 'emailjs-com';

const EnrollNowForm: React.FC = () => {
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const form = e.target as HTMLFormElement;

    // Send admin notification
    emailjs.sendForm(
      'service_j5327tt',
      'template_gj96kr2',
      form,
      '0GajrgILaKXY8q1Dd'
    ).then(() => {
      // Send user confirmation
      emailjs.send(
        'service_j5327tt',
        'template_yia6y5v',
        {
          user_email: form.email.value,
          user_name: form["firstName"].value + ' ' + form["lastName"].value,
        },
        '0GajrgILaKXY8q1Dd'
      );
      setSubmitted(true);
    }).catch(() => {
      alert('Failed to send email. Please try again.');
    });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-grafanaBg py-16 px-4">
      <div className="bg-grafanaGray rounded-2xl p-8 max-w-md w-full shadow-2xl border border-grafanaBlue/30">
        <h1 className="text-3xl md:text-4xl font-bold text-center mb-6 bg-gradient-to-r from-grafanaBlue via-blue-400 to-blue-300 bg-clip-text text-transparent">Enroll Now</h1>
        <p className="text-gray-300 text-center mb-8">Take the first step toward your dream tech career. Fill out the form to enroll now!</p>
        {submitted ? (
          <div className="text-center text-lg text-grafanaBlue font-semibold py-8">
            Thank you for enrolling! We will connect with you within 24 hours.
          </div>
        ) : (
          <form className="space-y-4" onSubmit={handleSubmit}>
            <input name="firstName" type="text" placeholder="First Name" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
            <input name="lastName" type="text" placeholder="Last Name" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
            <input name="mobile" type="tel" placeholder="Mobile Number" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
            <input name="email" type="email" placeholder="Email ID" className="w-full bg-grafanaBg border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:border-grafanaBlue focus:outline-none transition-colors duration-300" required />
            <button type="submit" className="w-full bg-gradient-to-r from-grafanaBlue via-blue-400 to-blue-300 text-white py-3 rounded-full text-lg font-semibold hover:shadow-grafana transition-all duration-300 transform hover:scale-105 mt-2">Submit</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default EnrollNowForm;
