import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar.tsx';
import HeroSection from './components/HeroSection.tsx';
import CoursesSection from './components/CoursesSection.tsx';
import CareerAddonsSection from './components/CareerAddonsSection.tsx';
import PricingSection from './components/PricingSection.tsx';
import TestimonialsSection from './components/TestimonialsSection.tsx';
import AboutSection from './components/AboutSection.tsx';
import ReferralSection from './components/ReferralSection.tsx';
import TrainersSection from './components/TrainersSection.tsx';
import ContactSection from './components/ContactSection.tsx';

const HomePage: React.FC = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="min-h-screen bg-grafanaBg text-white font-body">
      <Navbar isScrolled={isScrolled} />

      <main>
        <HeroSection />
        <CoursesSection />
        <CareerAddonsSection />
        <PricingSection />
        <TestimonialsSection />
        <AboutSection />
        <ReferralSection />
        <TrainersSection />
        <ContactSection />
      </main>

      <footer className="bg-grafanaGray py-8 text-center text-gray-400">
        <div className="container mx-auto px-4">
          <p>&copy; 2024 Cloud Blogger. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
