import React from 'react';

// Import real platform logos
import rhelLogo from '../assets/logos/rhel-logo.svg';
import dockerLogo from '../assets/logos/docker-logo.svg';
import azureLogo from '../assets/logos/azure-logo.svg';
import gitlabLogo from '../assets/logos/gitlab-logo.svg';
import prometheusLogo from '../assets/logos/prometheus-logo.svg';
import grafanaLogo from '../assets/logos/grafana-logo.svg';
import terraformLogo from '../assets/logos/terraform-logo.svg';
import kubernetesLogo from '../assets/logos/kubernetes-logo.svg';

const CoursesSection: React.FC = () => {
  const courses = [
    {
      id: 1,
      name: 'RHEL',
      description: 'Red Hat Enterprise Linux administration and management',
      logo: rhelLogo,
      color: 'from-red-500 to-red-600',
      duration: '4 weeks',
      level: 'Intermediate'
    },
    {
      id: 2,
      name: 'Docker',
      description: 'Containerization and container orchestration',
      logo: dockerLogo,
      color: 'from-blue-500 to-blue-600',
      duration: '3 weeks',
      level: 'Beginner'
    },
    {
      id: 3,
      name: 'Azure + Kubernetes',
      description: 'Microsoft Azure cloud and Kubernetes orchestration',
      logos: [azureLogo, kubernetesLogo],
      color: 'from-blue-600 to-blue-700',
      duration: '6 weeks',
      level: 'Advanced'
    },
    {
      id: 4,
      name: 'GitLab CI/CD',
      description: 'Continuous Integration and Continuous Deployment',
      logo: gitlabLogo,
      color: 'from-orange-500 to-orange-600',
      duration: '3 weeks',
      level: 'Intermediate'
    },
    {
      id: 5,
      name: 'Prometheus',
      description: 'Monitoring and alerting toolkit',
      logo: prometheusLogo,
      color: 'from-red-600 to-red-700',
      duration: '2 weeks',
      level: 'Intermediate'
    },
    {
      id: 6,
      name: 'Grafana',
      description: 'Data visualization and analytics platform',
      logo: grafanaLogo,
      color: 'from-orange-400 to-orange-500',
      duration: '2 weeks',
      level: 'Intermediate'
    },
    {
      id: 7,
      name: 'Terraform',
      description: 'Infrastructure as Code automation',
      logo: terraformLogo,
      color: 'from-purple-500 to-purple-600',
      duration: '4 weeks',
      level: 'Advanced'
    },
    {
      id: 8,
      name: 'AZ-104',
      description: 'Microsoft Azure Administrator certification',
      logo: azureLogo,
      color: 'from-blue-500 to-blue-600',
      duration: '8 weeks',
      level: 'Advanced'
    }
  ];

  return (
    <section id="courses" className="py-20 bg-grafanaGray">
      <div className="container mx-auto px-4">
        {/* Section Header */}
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-display font-bold mb-6">
            <span className="bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
              Master the Most
            </span>
            <br />
            <span className="bg-gradient-to-r from-grafanaBlue to-grafanaGreen bg-clip-text text-transparent">
              In-Demand Skills
            </span>
          </h2>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto">
            Comprehensive courses designed by industry experts to get you job-ready in the fastest-growing tech domains.
          </p>
        </div>

        {/* Courses Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {courses.map((course) => (
            <div
              key={course.id}
              className="relative bg-grafanaBg rounded-xl p-6 border border-gray-700 hover:border-grafanaBlue/50 transition-all duration-500 hover:transform hover:scale-105 group overflow-hidden"
            >
              {/* Hover Background Effect */}
              <div className="absolute inset-0 bg-gradient-to-br from-grafanaBlue/5 via-grafanaGreen/5 to-grafanaOrange/5 opacity-0 group-hover:opacity-100 transition-all duration-500"></div>

              {/* Animated Border Effect */}
              <div className="absolute inset-0 rounded-xl bg-gradient-to-r from-grafanaBlue via-grafanaGreen to-grafanaOrange opacity-0 group-hover:opacity-20 transition-all duration-500"></div>

              {/* Course Logo */}
              <div className="relative w-16 h-16 rounded-xl bg-gradient-to-br from-grafanaBlue/10 to-grafanaGreen/10 flex items-center justify-center mb-4 group-hover:scale-110 group-hover:rotate-3 transition-all duration-500">
                {course.logos ? (
                  <div className="flex items-center space-x-1">
                    {course.logos.map((logo, index) => (
                      <img key={index} src={logo} alt={`${course.name} logo ${index + 1}`} className="w-8 h-8 group-hover:drop-shadow-lg transition-all duration-300" />
                    ))}
                  </div>
                ) : (
                  <img src={course.logo} alt={`${course.name} logo`} className="w-10 h-10 group-hover:drop-shadow-lg transition-all duration-300" />
                )}
              </div>

              {/* Course Info */}
              <div className="relative">
                <h3 className="text-xl font-semibold text-white mb-2 group-hover:text-grafanaBlue transition-all duration-300 group-hover:scale-105">
                  {course.name}
                </h3>
                <p className="text-gray-400 text-sm mb-4 leading-relaxed group-hover:text-gray-300 transition-colors duration-300">
                  {course.description}
                </p>

                {/* Course Meta */}
                <div className="flex items-center justify-between">
                  <span className="flex items-center text-xs text-gray-500 group-hover:text-gray-400 transition-colors duration-300">
                    <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                    {course.duration}
                  </span>
                  <span className={`px-3 py-1 rounded-full text-xs font-medium transition-all duration-300 ${
                    course.level === 'Beginner' 
                      ? 'bg-gradient-to-r from-grafanaGreen/20 to-grafanaGreen/30 text-grafanaGreen border border-grafanaGreen/30 group-hover:from-grafanaGreen/30 group-hover:to-grafanaGreen/40' 
                      : course.level === 'Intermediate'
                      ? 'bg-gradient-to-r from-grafanaBlue/20 to-grafanaBlue/30 text-grafanaBlue border border-grafanaBlue/30 group-hover:from-grafanaBlue/30 group-hover:to-grafanaBlue/40'
                      : 'bg-gradient-to-r from-grafanaOrange/20 to-grafanaOrange/30 text-grafanaOrange border border-grafanaOrange/30 group-hover:from-grafanaOrange/30 group-hover:to-grafanaOrange/40'
                  }`}>
                    {course.level}
                  </span>
                </div>
              </div>

              {/* Floating Elements on Hover */}
              <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-all duration-500 delay-200">
                <div className="w-2 h-2 bg-grafanaGreen rounded-full animate-pulse"></div>
              </div>

              <div className="absolute bottom-2 left-2 opacity-0 group-hover:opacity-100 transition-all duration-500 delay-300">
                <div className="w-1 h-1 bg-grafanaBlue rounded-full animate-pulse"></div>
              </div>

              {/* Hover Content Overlay */}
              <div className="absolute inset-0 bg-gradient-to-br from-grafanaBlue/90 via-grafanaGreen/90 to-grafanaOrange/90 opacity-0 group-hover:opacity-100 transition-all duration-500 flex items-center justify-center rounded-xl">
                <div className="text-center text-white p-4 transform translate-y-4 group-hover:translate-y-0 transition-all duration-500">
                  <div className="text-2xl mb-2">🚀</div>
                  <h4 className="font-semibold mb-2">Ready to Master {course.name}?</h4>
                  <p className="text-sm opacity-90 mb-3">Join our comprehensive course and get hands-on experience</p>
                  <div className="inline-flex items-center px-3 py-1 bg-white/20 rounded-full text-xs">
                    <span>Click to learn more</span>
                    <svg className="w-3 h-3 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* CTA */}
        <div className="text-center mt-12">
          <button className="bg-gradient-to-r from-grafanaBlue to-grafanaGreen text-white px-8 py-4 rounded-full text-lg font-semibold hover:shadow-grafana transition-all duration-300 transform hover:scale-105">
            View All Course Details
          </button>
        </div>
      </div>
    </section>
  );
};

export default CoursesSection;
